using BenchmarkDotNet.Attributes;
using System;

namespace Mina.Benchmark;

public class StringBench
{
    [Benchmark]
    public void IndexOfCurrentCulture()
    {
        _ = "".IndexOf("bbbbbbbx", StringComparison.CurrentCulture);
    }

    [Benchmark]
    public void IndexOfOrdinal()
    {
        _ = "".IndexOf("bbbbbbbx", StringComparison.Ordinal);
    }
}
